// page init
jQuery(function(){
	initCarousel();
	initAccordion();
	initLightbox();
	initMobileNav();
	initSameHeight();
	initScrollHeader();
	initAnimPopup();
	initFlexMenu();
	initButtonAnim();
	initCustomHover();
});

// add classes on hover/touch
function initCustomHover() {
	jQuery('.register').touchHover();
}

function initButtonAnim() {
	// detect device type
	var isTouchDevice = /Windows Phone/.test(navigator.userAgent) || ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch;

	if (!isTouchDevice) jQuery('.community').touchHover();

	jQuery('.btn-block').each(function() {
		var holder = jQuery(this);
		holder.on('click', '.community', function(e) {
			e.preventDefault();
			holder.addClass('btn-active');
		});
	});
}

function initFlexMenu() {
	jQuery('.radio-form ul').each(function() {
		var list = jQuery(this);
		var radios = list.find('[type="radio"]');
		var checkedRadio = radios.filter(':checked');
		var radioTitle = list.data('moreText') || 'More';
		var timer;
		list.flexMenu({
			activeClass: 'drop-active',
			showOnHover: false,
			cutoff: 0,
			linkText: radioTitle
		});
		radios.on('change', function() {
			toggleItemClass(jQuery(this));
		});
		if (checkedRadio.length) toggleItemClass(checkedRadio);
		function toggleItemClass(el) {
			var viewMore = list.find('.flexMenu-viewMore');
			var popupRadio = list.find('.flexMenu-popup [type="radio"]');
			list.find('.flexMenu-viewMore').removeClass('drop-active').removeClass('radio-selected');
			if (el.closest(viewMore).length) {
				viewMore.addClass('radio-selected');
				viewMore.find('a').text(jQuery('label[for="' + el.attr('id') + '"]').text());
			} else {
				viewMore.find('a').text(radioTitle);
			}
		}
		jQuery(window).on('load resize orientationchange', function() {
			clearTimeout(timer);
			timer = setTimeout(function() {
				checkedRadio = radios.filter(':checked');
				if (checkedRadio.length) toggleItemClass(checkedRadio);
			}, 200);
		});
	});
}

function initAnimPopup() {
	var docElem = window.document.documentElement,
		didScroll, scrollPosition, centerPopupTimer;
	// detect WinPhone device
	var isWinPhoneDevice = /Windows Phone/.test(navigator.userAgent);

	// trick to prevent scrolling when opening/closing button
	function noScrollFn() {
		window.scrollTo(scrollPosition ? scrollPosition.x : 0, scrollPosition ? scrollPosition.y : 0);
	}

	function noScroll() {
		window.removeEventListener('scroll', scrollHandler);
		window.addEventListener('scroll', noScrollFn);
	}

	function scrollFn() {
		window.addEventListener('scroll', scrollHandler);
	}

	function canScroll() {
		window.removeEventListener('scroll', noScrollFn);
		scrollFn();
	}

	function scrollHandler() {
		if (!didScroll) {
			didScroll = true;
			setTimeout(function() {
				scrollPage();
			}, 60);
		}
	}

	function scrollPage() {
		scrollPosition = {
			x: window.pageXOffset || docElem.scrollLeft,
			y: window.pageYOffset || docElem.scrollTop
		};
		didScroll = false;
	}

	scrollFn();

	if (isWinPhoneDevice) jQuery('body').addClass('is-winphone');

	jQuery('.js-cp-opener').customAnimPopup({
		btnClose: '.close',
		onBeforeOpen: function(self) {
			// don't allow to scroll
			noScroll();

			jQuery('body').addClass('popup-open');
			self.popup.addClass('no-transition');

			if (isWinPhoneDevice) return;

			self.buttonPos = self.btnOpen[0].getBoundingClientRect();
			self.popup.add(self.popup.children()).css({
				'width': '',
				'height': 'auto'
			});
			self.realPopupHeight = self.popup.outerHeight();
			self.popup.children().css({
				'height': ''
			});
			self.popup.css({
				'top': self.buttonPos.top + 'px',
				'left': self.buttonPos.left + 'px',
				'width': self.btnOpen.outerWidth(),
				'height': self.btnOpen.outerHeight(),
				'marginTop': ''
			});
		},
		onOpen: function(self) {
			if (isWinPhoneDevice) return;

			self.popup.css({
				'top': '',
				'left': '',
				'width': '',
				'height': self.realPopupHeight,
				'marginTop': -self.realPopupHeight / 2
			});
			jQuery(window).on('scroll.centerPopup resize.centerPopup orientationchange.centerPopup', function() {
				clearTimeout(centerPopupTimer);
				centerPopupTimer = setTimeout(function() {
					self.realPopupHeight = self.popup.outerHeight();
					self.marginTop = -self.realPopupHeight / 2;
					self.popup.css({
						'marginTop': self.marginTop
					});
				}, 200);
			});
		},
		onAfterOpen: function(self) {
			// can scroll again
			canScroll();
			self.popup.addClass('no-transition');

			if (isWinPhoneDevice) return;

			self.popup.css({
				'top': '',
				'left': '',
				'width': '',
				'height': ''
			});
		},
		onBeforeClose: function(self) {
			// don't allow to scroll
			noScroll();
			self.popup.addClass('no-transition');

			if (isWinPhoneDevice) return;

			self.buttonPos = self.btnOpen[0].getBoundingClientRect();
			self.realPopupHeight = self.popup.outerHeight();
			self.marginTop = -self.realPopupHeight / 2;
			self.popup.css({
				'height': self.realPopupHeight,
				'marginTop': self.marginTop
			});
			jQuery(window).off('.centerPopup');
		},
		onClose: function(self) {
			jQuery('body').removeClass('popup-open');

			if (isWinPhoneDevice) return;

			self.popup.css({
				'top': self.buttonPos.top + 'px',
				'left': self.buttonPos.left + 'px',
				'width': self.btnOpen.outerWidth(),
				'height': self.btnOpen.outerHeight(),
				'marginTop': ''
			});
		},
		onAfterClose: function(self) {
			// can scroll again
			canScroll();
			self.popup.addClass('no-transition');

			if (isWinPhoneDevice) return;

			self.popup.css({
				'top': '',
				'left': '',
				'width': '',
				'height': '',
				'marginTop': ''
			});
		}
	});
}

// init scroll header
function initScrollHeader() {
	jQuery('.fixed-header').headroom({
		offset: 100,
		tolerance: {
			up: 30,
			down: 5
		},
		classes: {
			initial: 'headroom',
			pinned: 'headroom-pinned',
			unpinned: 'headroom-unpinned',
			top: 'headroom-top',
			notTop: 'headroom-not-top'
		}
	});
}

// scroll gallery init
function initCarousel() {
	jQuery('.tab-carousel-holder').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.tabset-carousel li',
		stretchSlideToMask: true,
		maskAutoSize: true,
		autoRotation: false,
		switchTime: 3000,
		step: 1,
		onInit: function(self) {
			ResponsiveHelper.addRange({
				'..767': {
					on: function() {
						self.options.animSpeed = 300;
					}
				},
				'768..': {
					on: function() {
						self.options.animSpeed = 0;
					}
				}
			});
		}
	});
}

// accordion menu init
function initAccordion() {
	jQuery('ul.accordion').slideAccordion({
		opener: 'a.opener',
		slider: 'div.slide',
		animSpeed: 300
	});
}

// fancybox modal popup init
function initLightbox() {
	jQuery('a.lightbox, a[rel*="lightbox"]').fancybox({
		padding: 0,
		loop: false,
		helpers: {
			overlay: {
				css: {
					background: 'rgba(0, 0, 0, 0.65)'
				}
			}
		},
		afterLoad: function(current, previous) {
			// handle custom close button in inline modal
			if(current.href.indexOf('#') === 0) {
				jQuery(current.href).find('a.close').off('click.fb').on('click.fb', function(e){
					e.preventDefault();
					jQuery.fancybox.close();
				});
			}
		}
	});
}

// mobile menu init
function initMobileNav() {
	// handle layout resize
	ResponsiveHelper.addRange({
		'..767': {
			on: function() {
				jQuery('html').mobileNav({
					menuActiveClass: 'nav-active',
					menuOpener: '.nav-opener',
					menuDrop: '.drop-holder',
					onBeforeShow: function() {
						this.scrollTop = jQuery(window).scrollTop();
						jQuery('#wrapper').css({
							'position': 'relative',
							'top': -this.scrollTop
						});
					},
					onAfterHide: function() {
						jQuery('#wrapper').css({
							'position': '',
							'top': ''
						});
						jQuery(window).scrollTop(this.scrollTop);
					}
				});
			},
			off: function() {
				jQuery('html').each(function() {
					var inst = jQuery(this).data('MobileNav');
					if (inst) {
						var scrollTop = inst.scrollTop;
						inst.destroy();
						jQuery('#wrapper').css({
							'position': '',
							'top': ''
						});
						jQuery(window).scrollTop(scrollTop);
					}
				});
			}
		}
	});
}

// align blocks height
function initSameHeight() {
	jQuery('.two-columns').sameHeight({
		elements: '.column',
		flexible: true,
		multiLine: true
	});
	jQuery('.three-columns').sameHeight({
		elements: '.column',
		flexible: true,
		multiLine: true
	});
	jQuery('.heetch-columns').sameHeight({
		elements: '.column',
		flexible: true,
		multiLine: true
	});
}
